using xggameplan.Common;

namespace xggameplan.Model
{
    public class AutopilotPassModel : PassModel
    {
        public AutopilotPassType? AutopilotType { get; set; }
        public bool IsAutopilot => AutopilotType != null;
        public bool IsAutoGenerated => AutopilotType == AutopilotPassType.TightenALot ||
                                       AutopilotType == AutopilotPassType.TightenABit ||
                                       AutopilotType == AutopilotPassType.LoosenABit ||
                                       AutopilotType == AutopilotPassType.LoosenALot;
    }
}
